import tkinter as tkimport matplotlibmatplotlib.use("TkAgg")from matplotlib.backends.backend_tkagg import FigureCanvasTkAgg, NavigationToolbar2TkAggfrom matplotlib.figure import Figureclass Window(tk.Frame):    def __init__(self, master=None):        tk.Frame.__init__(self, master)                self.master = master        menu = tk.Menu(self.master)        self.master.config(menu=menu)                fileMenu = tk.Menu(menu)        fileMenu.add_command(label="New")        menu.add_cascade(label="File", menu=fileMenu)                editMenu = tk.Menu(menu)        editMenu.add_command(label="Undo")        editMenu.add_command(label="Redo")        menu.add_cascade(label="Edit", menu=editMenu)root = tk.Tk()app = Window(root)root.wm_title("ElectricFields")root.geometry("800x800")root.mainloop()